package goapi

import (
	"bytes"
	"context"
	"encoding/json"
	"fmt"
	"io/ioutil"
	"log"
	"net/http"
	"net/http/httptest"
	"reflect"
	"testing"
	"time"
)

const (
	succeed = "\u2713"
	failed  = "\u2717"
	red     = "\033[31m"
	green   = "\033[32m"
	reset   = "\033[0m"
)

type EmptyObj struct {
	Obj string `json:"obj,omitempty"`
}

type AuthorizeStruct struct {
	name    string
	ctx     context.Context
	cfg     *Config
	input   *InputPayload
	wantRes ResultMap
	wantErr bool
}

var result interface{}

// goapi % go test -bench=Autorizer_Authorize -benchtime=10s -run=dontrunanytests
//ToDo: Benchmark parallel executions
func BenchmarkAutorizer_Authorize(b *testing.B) {

	svr := getSvr("")
	defer svr.Close()

	cfg := &Config{
		Applicaton:    "test-app",
		DecisionPath:  DefaultDecisionPath,
		OPAConfigFile: createOPAConfigFile(svr.URL, "/bundles/bundle.tar.gz", ""),
	}

	a, err := NewAutorizer(cfg)
	if err != nil {
		b.Fatal(err)
	}

	tests := getTestsNoErr()
	var res interface{}

	for i := 0; i < b.N; i++ {
		//getSvr("")

		for _, tt := range tests {
			res, _ = a.Authorize(tt.ctx, tt.cfg, tt.input)
		}
		result = res
	}

}

func getSvr(filename string) *httptest.Server {
	// https://github.com/stevef1uk/opa-bundle-server
	hf := func(w http.ResponseWriter, r *http.Request) {
		log.Printf("Received a request: %+v", r)
		data, err := ioutil.ReadFile("./data_test/bundle.tar.gz")
		if err != nil {
			log.Fatal(err)
		}
		w.Header().Set("Content-Type", "application/octet-stream")
		w.Header().Set("Content-Disposition", "attachment; filename="+"bundle.tar.gz")
		w.Header().Set("Content-Transfer-Encoding", "binary")
		w.Header().Set("Expires", "0")
		http.ServeContent(w, r, "Fred", time.Now(), bytes.NewReader(data))
	}

	svr := httptest.NewServer(http.HandlerFunc(hf))
	return svr
}

func getTestsNoErr() []AuthorizeStruct {
	return []AuthorizeStruct{
		{
			name: "SmokeTestOk",
			ctx:  context.Background(),
			cfg:  &Config{},
			input: &InputPayload{
				Endpoint:    "TagService.List",
				Application: "atlas.tagging",
				FullMethod:  "/service.TagService/List",
				JWT:         "eyJhbGciOiJSUzUxMiIsInR5cCI6IkpXVCJ9..redacted",
				RequestID:   "6c9a32b2-57f3-40c0-a4d3-1b22a8e15f16",
				DecisionInput: DecisionInput{
					Type:             "",
					Verb:             "",
					SealCtx:          nil,
					DecisionDocument: "",
				},
			},
			wantRes: ResultMap{
				"allow":       false,
				"obligations": EmptyObj{},
			},

			wantErr: false,
		},
	}
}

func Test_autorizer_Authorize(t *testing.T) {
	// https://github.com/stevef1uk/opa-bundle-server
	hf := func(w http.ResponseWriter, r *http.Request) {
		t.Logf("Received a request: %+v", r)
		data, err := ioutil.ReadFile("./data_test/bundle.tar.gz")
		if err != nil {
			t.Fatal(err)
		}
		w.Header().Set("Content-Type", "application/octet-stream")
		w.Header().Set("Content-Disposition", "attachment; filename="+"bundle.tar.gz")
		w.Header().Set("Content-Transfer-Encoding", "binary")
		w.Header().Set("Expires", "0")
		http.ServeContent(w, r, "Fred", time.Now(), bytes.NewReader(data))
	}

	svr := httptest.NewServer(http.HandlerFunc(hf))
	defer svr.Close()

	cfg := &Config{
		Applicaton:    "test-app",
		DecisionPath:  DefaultDecisionPath,
		OPAConfigFile: createOPAConfigFile(svr.URL, "/bundles/bundle.tar.gz", ""),
	}

	a, err := NewAutorizer(cfg)
	if err != nil {
		t.Fatal(err)
	}

	tests := []struct {
		name    string
		ctx     context.Context
		cfg     *Config
		input   *InputPayload
		wantRes ResultMap
		wantErr bool
	}{
		{
			name: "SmokeTestOk",
			ctx:  context.Background(),
			cfg:  &Config{},
			input: &InputPayload{
				Endpoint:    "TagService.List",
				Application: "atlas.tagging",
				FullMethod:  "/service.TagService/List",
				JWT:         "eyJhbGciOiJSUzUxMiIsInR5cCI6IkpXVCJ9..redacted",
				RequestID:   "6c9a32b2-57f3-40c0-a4d3-1b22a8e15f16",
				DecisionInput: DecisionInput{
					Type:             "",
					Verb:             "",
					SealCtx:          nil,
					DecisionDocument: "",
				},
			},
			wantRes: ResultMap{
				"allow":       false,
				"obligations": EmptyObj{},
			},

			wantErr: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			res, err := a.Authorize(tt.ctx, tt.cfg, tt.input)

			// TODO
			var resMap ResultMap
			if err == nil {
				resMap, err = parseResult(tt.ctx, res)
			}

			// check error
			if err != nil {
				if !tt.wantErr {
					t.Errorf("\t%s unexpected error when running %s test"+
						"\nGot: %s\nWant error: %t", failed, tt.name, err.Error(), tt.wantErr)
					return
				} else {
					t.Logf("\t%s %s test is passed", succeed, tt.name)
					return
				}
			}

			// check result
			t.Logf("Result map: %+v", resMap)

			resJSON, err := json.MarshalIndent(resMap, "", "    ")
			if err != nil {
				t.Errorf("JSON marshal error %v", err)
				return
			}

			wantResJSON, err := json.MarshalIndent(tt.wantRes, "", "    ")
			if err != nil {
				t.Errorf("JSON marshal error %v", err)
				return
			}

			if !reflect.DeepEqual(resJSON, wantResJSON) {
				vs := fmt.Sprintf("\t%s difference in got vs want autorization decision result "+
					"\nGot: "+red+" \n\n%s\n\n "+reset+"\nWant: "+green+"\n\n%s\n\n"+reset,
					failed, string(resJSON), string(wantResJSON))
				t.Errorf(vs)
				return
			}

			t.Logf("\t%s %s test is passed", succeed, tt.name)
		})
	}
}
